import React, { Component } from "react";
import { storeProducts, detailProduct } from "./data";
const ProductContext = React.createContext();
//Provider : provides data
//Consumer : access data

class ProductProvider extends Component {
  state = {
    products: [],
    detailProduct: detailProduct,
    cart: [],
    modalOpen: false,
    modalProduct: detailProduct,
    cartSubTotal: 0,
    cartTax: 0,
    cartTotal: 0
  };
  componentDidMount() {
    this.setProducts();
  }
  setProducts = () => {
    let tempproducts = [];
    storeProducts.forEach(item => {
      const singleitem = { ...item }; //three dots indicate comping value not referencing
      tempproducts.push(singleitem);
    });
    this.setState(() => {
      return { products: tempproducts };
    });
  };

  getItem = id => {
    const product = this.state.products.find(item => item.id === id);
    return product;
  };

  handleDetail = id => {
    const product = this.getItem(id);
    console.log(product);
    this.setState(() => {
      return { detailProduct: product };
    });
  };

  addToCart = id => {
    let tempProduct = [...this.state.products];
    let tempcart = [...this.state.cart];
    const index = this.state.products.indexOf(this.getItem(id));
    const product = tempProduct[index];
    product.inCart = true;
    product.count = 1;
    const price = product.price;
    product.total = price;
    tempcart.push(product);
    this.setState(
      () => {
        return {
          products: tempProduct,
          cart: tempcart
        };
      },
      () => {
        this.getTotals();
      }
    );
  };
  openModal = id => {
    const product = this.getItem(id);
    this.setState(() => {
      return {
        modalProduct: product,
        modalOpen: true
      };
    });
  };

  closeModal = () => {
    this.setState(() => {
      return {
        modalOpen: false
      };
    });
  };

  increment = id => {
    console.log("hello from increment method");
  };
  decrement = id => {
    let tempcart = [...this.state.cart];
    const index = tempcart.indexOf(this.getItem(id));
    const product = tempcart[index];
    if (product.count == 1) {
      product.count == 1;
    }
    this.setState(
      () => {
        return {
          products: tempProduct,
          cart: tempcart
        };
      },
      () => {
        this.getTotals();
      }
    );
  };
  removeItem = id => {
    let tempProduct = [...this.state.products];
    let tempcart = [...this.state.cart];
    const index = tempcart.indexOf(this.getItem(id));
    const product = tempcart[index];
    tempcart.splice(index, 1);
    product.inCart = false;
    this.setState(
      () => {
        return {
          products: tempProduct,
          cart: tempcart
        };
      },
      () => {
        this.getTotals();
      }
    );
  };
  clearCart = () => {
    console.log("hello from clear cart method");
    this.setState(
      () => {
        return {
          cart: []
        };
      },
      () => {
        this.setProducts();
      }
    );
  };
  getTotals = () => {
    const cart = [...this.state.cart];
    let subtotal = 0;
    let tax = 0;
    let grandtotal = 0;
    cart.map(item => {
      subtotal += item.total;
    });
    tax = subtotal * 0.13;
    grandtotal = subtotal + tax;
    this.setState(() => {
      return {
        cartSubTotal: subtotal.toFixed(2),
        cartTax: tax.toFixed(2),
        cartTotal: grandtotal.toFixed(2)
      };
    });
  };

  render() {
    return (
      <ProductContext.Provider
        value={{
          ...this.state,
          handleDetail: this.handleDetail,
          addToCart: this.addToCart,
          openModal: this.openModal,
          closeModal: this.closeModal,
          increment: this.increment,
          decrement: this.decrement,
          removeItem: this.removeItem,
          clearCart: this.clearCart
        }}
      >
        {this.props.children}
      </ProductContext.Provider>
    );
  }
}
const ProductConsumer = ProductContext.Consumer;

export { ProductProvider, ProductConsumer };
